export const ringStyles = {
  background: "white",
  width: "40vh",
  height: "40vh",
  display: "flex",
  borderRadius: "100%",
  margin: "auto",
};

export const innerRingStyles = {
  background: "white",
  width: "35vh",
  height: "35vh",
  display: "flex",
  borderRadius: "100%",
  margin: "auto",
};

export const mainStyles = {
  width: "100vw",
  height: "100vh",
  display: "flex",
  alignContent: "center",
  flexWrap: "wrap",
  alignItems: "center",
  background: "gray",
  margin: "auto",
};

export const secondsNeedle = {
  width: "30vh",
  height: "1rem",
  background: "#3A3A3A",
  transform: "rotate(90deg)",
  display: "block",
  position: "relative",
  left: "25%",
  top: "25%",
};
